import React from 'react'
import { Row, Col, Breadcrumb, BreadcrumbItem, Button, Label, Input } from 'reactstrap'
import DisplayImage from './DisplayObjectImage';
import ObjectDescription from './ObjectDescription';
import {faShoppingCart} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

const objectPage = ( props ) => {
    const boxStyle = {
        padding: '3%',
    };

    const buttonStyle = {
        width: '100%'
    };

    const radioStyle = {
        paddingLeft: '12%'
    };

    const priceStyle = {
        textAlign: 'right'
    };

    const priceOption = {
        textAlign: 'left'
    };

    return(
        <div>
            <Row>
                <Col xs={5}>
                    <div style={boxStyle}>
                        <Breadcrumb>
                            <BreadcrumbItem><a href="http://localhost:3000/">Home</a></BreadcrumbItem>
                            <BreadcrumbItem active>The Amazing Sloth</BreadcrumbItem>
                        </Breadcrumb>
                        <DisplayImage img={ props.img }/>
                    </div>
                </Col>
                <Col style={boxStyle}>
                    <h1>The Amazing Sloth</h1>
                    <ObjectDescription />
                </Col>
                <Col style={boxStyle}>
                    <div className='shadow p-3 mb-5 bg-white rounded'>
                        <h4>$598.23</h4>
                        <hr />
                        <Button color='warning' style={buttonStyle}>
                            Add to Cart <FontAwesomeIcon icon={faShoppingCart} size='1x' color='black'/>
                        </Button>
                        <hr />
                        <div style={radioStyle}>
                            <Row>
                                <Col>
                                <Label style={priceOption}>
                                    <Input type='radio' checked='checked' />{' '}
                                    Buy New
                                </Label>
                                </Col>
                                <Col>
                                    <p style={priceStyle}>$598.23</p>
                                </Col>
                            </Row>
                            <Row>
                                <Label>
                                    <Input type='radio' />{' '}
                                    Buy Used
                                </Label>
                            </Row>
                        </div>
                        <hr />
                    </div>
                </Col>
            </Row>
        </div>
    )
};

export default objectPage;
