import React, { useState } from 'react';
import './App.css';
import ObjectPage from './ObjectPage/ObjectPage';
import Navbar from './HeaderComponent/HeaderComponent';
import Footer from './FooterComponent/FooterComponent';

function App() {

    const [price, setPrice] = useState({
        priceOption: 'new',
        price: 598.23,
        quantity: 1
    });

    const priceHandler = (event) => {
        let price;
        const option = event.target.value;
        if (option === 'new') {
            price = 598.23;
        } else {
            price = 478.13;
        }
        setPrice({priceOption: option, price: (price * price.quantity)})
    };

    const quantityHandler = (event) => {
        setPrice({quantity: event.target.value, price: (price.price * event.target.value)})
    };

    const backgroundStyle = {
        backgroundColor: '#F0F8FF'
    };

  return (
    <div className="App" style={backgroundStyle}>
        <Navbar />
        <ObjectPage img='https://c402277.ssl.cf1.rackcdn.com/photos/14785/images/story_full_width/shutterstock_532108075.jpg?1512507049'
        currentPriceOption={price.priceOption} changePriceOption={priceHandler} price={price.price}
                    quantity={price.quantity} changeQuantity={quantityHandler}/>
        <Footer />
    </div>
  );
}

export default App;
